const express = require('express');
const axios = require('axios');
const cors = require('cors');

const app = express();
const PORT = 5000;

const RIOT_API_KEY = 'RGAPI-여기에-너의-키';

app.use(cors()); // ❗ 꼭 이 줄 있어야 함!

app.get('/account', async (req, res) => {
  const { username, tagline } = req.query;

  try {
    const response = await axios.get(
      `https://asia.api.riotgames.com/riot/account/v1/accounts/by-riot-id/${encodeURIComponent(username)}/${encodeURIComponent(tagline)}`,
      {
        headers: {
          'X-Riot-Token': RIOT_API_KEY
        }
      }
    );

    res.json(response.data);
  } catch (error) {
    res.status(500).json({
      message: 'API 요청 실패',
      error: error.toString()
    });
  }
});

app.listen(PORT, () => {
  console.log(`🚀 서버 실행 중: http://localhost:${PORT}`);
});
