// EsportsPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const leagues = ['전체', 'PACIFIC', 'EMEA', 'NA', 'CN'];

const leagueImages = {
  PACIFIC: '/leagues/PACIFIC.jpg',
  EMEA: '/leagues/EMEA.jpg',
  NA: '/leagues/NA.jpg',
  CN: '/leagues/CN.jpg',
};

function EsportsPage() {
  const navigate = useNavigate();
  const [selectedLeague, setSelectedLeague] = useState('전체');

  const handleLeagueSelect = (league) => {
    setSelectedLeague(league);
  };

  return (
    <div style={styles.pageWrapper}>
      <nav style={styles.navbar}>
        <span style={styles.logo} onClick={() => navigate('/')}>INFOV</span>
        <div style={styles.navItems}>
          <span style={styles.navItem} onClick={() => navigate('/agents')}>요원</span>
          <span style={styles.navItem} onClick={() => navigate('/maps')}>맵 로테이션</span>
          <span style={styles.navItem} onClick={() => navigate('/rank')}>랭크</span>
          <span style={{ ...styles.navItem, fontWeight: 'bold', fontSize: '20px' }}>E-Sports</span>
        </div>
      </nav>

      <div style={styles.content}>
        <div style={styles.leagueButtons}>
          {leagues.map((league) => (
            <button
              key={league}
              style={{
                ...styles.leagueButton,
                backgroundColor: selectedLeague === league ? '#4A90E2' : '#e0e0e0',
                color: selectedLeague === league ? 'white' : '#333',
              }}
              onClick={() => handleLeagueSelect(league)}
            >
              {league}
            </button>
          ))}
        </div>

        <div style={styles.imageGrid}>
          {(selectedLeague === '전체' ? leagues.filter(l => l !== '전체') : [selectedLeague])
            .map((league) => (
              <div key={league} style={styles.leagueCard}>
                <img
                  src={leagueImages[league]}
                  alt={league}
                  style={styles.leagueImage}
                />
                <div style={styles.leagueLabel}>{league} 리그</div>
              </div>
          ))}
        </div>
      </div>
    </div>
  );
}

const styles = {
  pageWrapper: {
    backgroundColor: '#f5f5f5',
    minHeight: '100vh',
  },
  navbar: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '40px',
    padding: '20px 40px',
    backgroundColor: '#ffffff',
    borderBottom: '1px solid #ddd',
    position: 'relative',
  },
  logo: {
    position: 'absolute',
    left: '40px',
    fontSize: '24px',
    fontWeight: 'bold',
    color: '#000',
    cursor: 'pointer',
  },
  navItems: {
    display: 'flex',
    gap: '30px',
  },
  navItem: {
    fontSize: '18px',
    color: '#333',
    cursor: 'pointer',
  },
  content: {
    paddingTop: '120px',
    textAlign: 'center',
  },
  leagueButtons: {
    display: 'flex',
    justifyContent: 'center',
    gap: '10px',
    marginBottom: '30px',
    flexWrap: 'wrap',
  },
  leagueButton: {
    padding: '10px 20px',
    fontSize: '16px',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
  },
  imageGrid: {
    display: 'flex',
    justifyContent: 'center',
    gap: '20px',
    flexWrap: 'wrap',
  },
  leagueCard: {
    width: '200px',
    textAlign: 'center',
  },
  leagueImage: {
    width: '100%',
    aspectRatio: '1 / 1',
    objectFit: 'cover',
    borderRadius: '10px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
  },
  leagueLabel: {
    marginTop: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  },
};

export default EsportsPage;
